import React, { useEffect, useState } from "react";
import ListingCard from "../ListingCard";
import items from "../../mockData/items.json";
import ItemList from "../itemList/ItemList";

function HomePage() {
  const [listings, setListings] = useState([]);

  // Simulate fetching listings from an API (you should replace this with a real API call)
  useEffect(() => {
    // Replace this with your API fetch logic
    // For this example, we're using mock data
    const mockListings = [
      {
        id: 1,
        title: "Cozy Apartment",
        description: "A beautiful apartment in the city center.",
        price: 150000,
        image: "apartment-image.jpg",
      },
      {
        id: 2,
        title: "Luxury Villa",
        description: "A stunning villa with a pool and garden.",
        price: 500000,
        image: "villa-image.jpg",
      },
      // Add more listings here
    ];

    setListings(mockListings);
  }, []);

  return (
    <div className="homepage">
      <h1>Featured Listings</h1>
      <div className="listing-cards">
        {listings.map((listing) => (
          <ListingCard
            key={listing.id}
            title={listing.title}
            description={listing.description}
            price={listing.price}
            image={listing.image}
          />
        ))}
      </div>
    </div>
  );
}
export default HomePage;
